                Priority = 0,
                Description = "",
                State = State.NOTSTARTED,
                AbsoluteLevel = 0,
                RelativeLevel = 0
            });

        // sort twigs and update nested structure
        TwigsTempStorage.Order();

        TwigsTempStorage.OpenForEdittingId = newTwigId;
    }

